function visualize_trajectories_scatter(Trajectories, Gpoints_all, Opoints_all, ttle)    % Trajectories is a cell array of trajectories    ntraj = length(Trajectories);    fig = figure;    hold on;    % Create the scatter plot    for i = 1:ntraj        traj = Trajectories{i};        x3 = traj(:, 1);        y3 = traj(:, 2);        plot(x3, y3, 'r.-');    end    %figure, hold on    x1 = Opoints_all(:, 1);    y1 = Opoints_all(:, 2);    scatter(x1, y1, 1, 'k', 's');     x2 = Gpoints_all(:, 1);    y2 = Gpoints_all(:, 2);    scatter(x2, y2, 2, 'g', 's');        % Add a title with a smaller font size    title(ttle, 'FontSize', 10, 'FontWeight', 'bold');        % Add x, y, z labels with smaller font size    xlabel('x (m)', 'FontSize', 9);    xlim([0,1020])    ylabel('y (m)', 'FontSize', 9);    ylim([0,1020])    %axis([xmin xmax ymin ymax zmin zmax]);    % Add grid and adjust font size for axes    grid on;    set(gca, 'FontSize', 8); % Tick labels and other axes text        % Adjust figure size for two-column layout    set(gcf, 'Units', 'inches', 'Position', [0, 0, 3.5, 3]); % Width = 3.5 inches for one column        hold off;        saveas(fig, strcat(ttle,'.fig')); % Save as PNG    end